mapper.insert:
INSERT INTO invitation
number, account, time, title, Subtitle, text, name
#{number, jdbcType=INTEGER}, #{account, jdbcType=VARCHAR} , #{time, jdbcType=INTEGER}, #{title, jdbcType=INTEGER}, #{Subtitle, jdbcType=INTEGER}, #{text, jdbcType=INTEGER}, #{name, jdbcType=INTEGER}

mapper.select:
SELECT *
FROM invitation
WHERE number = #{number, jdbcType = INTEGER}

SELECT *
FROM invitation
WHERE account = {account, jdbcType = VARCHAR}
